    },
    "item": [
        {
            "linkId": "4",
            "text": "Deteksi dini PPOK (Khusus usia >40 tahun dan merokok)",
            "item": [
                {
                    "linkId": "4.1",
                    "text": "Apakah peserta pernah merokok?",
                    "answer": [
                        {
                            "valueBoolean": true
                        }
                    ]
                },
                {
                    "linkId": "4.2",
                    "text": "Berapa rata-rata jumlah batang rokok per hari",
                    "answer": [
                        {
                            "valueInteger": 10
                        }
                    ]
                },
                {
                    "linkId": "4.3",
                    "text": "Lama merokok dalam tahun",
                    "answer": [
                        {
                            "valueQuantity": {
                                "value": 5,
                                "code": "a",
                                "system": "http://unitsofmeasure.org",
                                "unit": "a"
                            }
                        }
                    ]
                },
                {
                    "linkId": "4.4",
                    "text": "Status merokok",
                    "answer": [
                        {
                            "valueReference": {
                                "reference": "Observation/{{Observation_merokok}}"
                            }
                        }
                    ]
                },
                {
                    "linkId": "4.5",
                    "text": "Apakah peserta pernah merasa napas pendek ketika peserta berjalan lebih cepat pada jalan yang datar atau pada jalan yang sedikit menanjak?",
                    "answer": [
                        {
                            "valueBoolean": true
                        }
                    ]
                },
                {
                    "linkId": "4.6",
                    "text": "Apakah peserta biasanya mempunyai dahak yang berasal dari paru atau kesulitan mengeluarkan dahak saat peserta sedang tidak menderita selesma/flu?",
                    "answer": [
                        {
                            "valueBoolean": true
                        }
                    ]
                },
                {
                    "linkId": "4.7",
                    "text": "Apakah peserta biasanya batuk saat peserta sedang tidak menderita selesma/flu?",
                    "answer": [
                        {
                            "valueBoolean": false
                        }
                    ]
                },
                {
                    "linkId": "4.8",
                    "text": "Apakah Dokter atau tenaga medis lainnya pernah meminta peserta untuk melakukan pemeriksaan spirometri atau peak flow meter (meniup ke dalam suatu alat) untuk mengetahui fungsi paru peserta?",
                    "answer": [
                        {
                            "valueBoolean": false
                        }
                    ]
                },
                {
                    "linkId": "4.9",
                    "text": "Hasil Kuesioner PUMA",
                    "answer": [
                        {
                            "valueCoding": {
                                "system": "http://snomed.info/sct",
                                "code": "723505004",
                                "display": "Low risk"
                            }
                        }
                    ]
                }
            ]
        }
    ]
}

